function sumAll(arr) {
  const sortedArr = arr.sort((a, b) => a - b)
  const startCount = Number(sortedArr.slice(0, 1)) + 1 
  const endCount = sortedArr.slice(1)
  const rangeNum = []
  for (let i = startCount; i < endCount; i++) {
   rangeNum.push(i)
  } 
  return  [...sortedArr, ...rangeNum].reduce((acc, val) => acc + val)
}


