function mutation(arr) {
	const splitted = arr.map(str => str.toLowerCase().split(''))
  const str1 = splitted[0]
  const str2 = splitted[1]

  const result = []

  for(let i = 0; i < str2.length; i++) {
  	result.push(str1.includes(str2[i]))
  }

  return result.every(value =>  value === true)
}

mutation(["hello", "hey"]);
